import { Component } from "@angular/core";
import { Book } from "./book";

@Component({
    selector: 'book-list',
    templateUrl: './book-list.component.html'
})

export class BookListComponent {

    private BOOKS: Book[] = [
        new Book('123456', 'Podstawy C#', 'Scot Hanselman', 'Tech', 'Książka dla programistów'),
        new Book('456789', 'C# Zaawansowany', 'Scot Hanselman', 'Tech', 'Książka dla zaawansowanych programistów'),
        new Book('678910', 'Specjalista C#', 'Scot Hanselman', 'Tech', 'Książka dla specjalistów C#'),
    ]

public books: Book[] = [];
public selectedBook: Book = this.BOOKS[0];

ngOnInit(): void {
    this.getBooks().then(b => this.books = b)
}

    getBooks(): Promise<Book[]> {
        return Promise.resolve(this.BOOKS);
    } 

    onClick(book: Book) {
        this.selectedBook = book;
    }
}
