import { Component } from "@angular/core";
import { Book } from "./book";

@Component({
    selector: 'book-list',
    templateUrl: './book-list.component.html'
})

export class BookListComponent {

    private BOOKS: Book[] = [
        new Book('123456', 'Podstawy C#', 'Scot Hanselman', 'Tech', 'Książka dla programistów'),
        new Book('456789', 'C# Zaawansowany', 'Scot Hanselman', 'Tech', 'Książka dla zaawansowanych programistów'),
        new Book('678910', 'Specjalista C#', 'Scot Hanselman', 'Tech', 'Książka dla specjalistów C#'),
    ]

public books: Book[] = [];
public selectedBook: Book = undefined as any;
// public selectedBook: Book | undefined = undefined;
// public selectedBook?: Book;

ngOnInit(): void {
    this.getBooks().then(b => this.books = b)
}

    getBooks(): Promise<Book[]> {
        return Promise.resolve(this.BOOKS);
    } 

    onClick(book: Book) {
        this.selectedBook = book;
    }
}


<h3>Books</h3>
<ul>
    <li *ngFor="let book of books">
        <span>{{book.ISBN}}</span>
        <span>{{book.title}}</span>
        <span>{{book.author}}</span>
        <button (click)="onClick(book)">Zaznacz</button>
    </li>
</ul>

<div *ngIf="selectedBook">
    <display-book 
        [ISBN]="selectedBook.ISBN" 
        [author]="selectedBook.author" 
        [title]="selectedBook.title" 
        [genre]="selectedBook.genre" 
        [description]="selectedBook.description"
        (selectedBook)="onClick(selectedBook)"
        >
        
    </display-book>
    <button (click)="onClick(selectedBook)">Odznacz</button>
</div>
