import { NgFor } from "@angular/common";
import {Component} from "@angular/core";
import { Person } from "./person";
import { LogService } from "../services/log.service";
import { Route, Router } from "@angular/router";


@Component({
    selector: 'person-form',
    templateUrl: './person-form.component.html'
})

export class PersonFormComponent
{
    constructor(private logService: LogService, private router: Router) {}

    public person: Person = new Person('', '', 0, '', new Date());

    public professions = ['Developer', 'Architekt', 'Programista'];

    public onFormSubmit()
    {
        alert(JSON.stringify(this.person));
        this.logService.info(JSON.stringify(this.person));
    }

    public btnClick()
    {
        alert(JSON.stringify(this.person));
        this.logService.info(JSON.stringify(this.person));


        this.isNameNullOrEmpty = true;
        this.isLastNameNullOrEmpty = true;

        if(this.person.name === '')
        {
            this.isNameNullOrEmpty = true;
        } else {
            this.isNameNullOrEmpty = false;
        }

        if(this.person.name === '')
        {
            this.isLastNameNullOrEmpty = true;
        } else {
            this.isLastNameNullOrEmpty = false;
        }
    }

    public isNameNullOrEmpty: boolean = false;
    public isLastNameNullOrEmpty: boolean = false;
    public goToEdit() {
        this,this.router.navigate(['/edit', 1]);
    }
}


<input class="form-control"/>

<div class="container">
    <form (ngSubmit)="onFormSubmit()" #postForm="ngForm">
        <h3>Formularz dodawania osoby</h3>
    <div class="form-group">
        <label for="name">Imię</label>
        <input type="text" class="form-control" id="name" name="name" [(ngModel)]="person.name"/>

        <div class="alet alert-danger" [hidden]="!isNameNullOrEmpty">
            Imię jest wymagane
        </div>
    </div>

    <div class="form-group">
        <label for="lastName">Nazwisko</label>
        <input type="text" class="form-control" id="lastName" name="lastName" [(ngModel)]="person.lastName"/>

        <div class="alet alert-danger" [hidden]="!isLastNameNullOrEmpty">
            Nazwisko jest wymagane
        </div>
    </div>

    <div class="form-group">
        <label for="age">Wiek</label>
        <input type="number" class="form-control" id="age" name="age" [(ngModel)]="person.age"/>
    </div>

    <div class="form-group">
        <label for="birthDate">Data urodzenia</label>
        <input type="date" class="form-control" id="birthDate" name="birthDate" [(ngModel)]="person.birthDate"/>
    </div>

    <div class="form-group">
        <label for="profession">Profesja</label>
        <select class="form-control" id="profession" name="profession" [(ngModel)]="person.profession">
            <option *ngFor="let prof of professions" [value]="prof">{{prof}}</option>
        </select>
    </div>

<input type="submit" />
<button click="goToEdit()">Przejdź do edycji</button>

<!-- <button class="btn btn-succes" (click)="btnClick()">Zapisz</button> -->
    </form>
</div>











