import { Pipe, PipeTransform } from "@angular/core";

@Pipe({
    name: 'nameFormat'
})

export class NamePipe implements PipeTransform {

    transform(value: string) {
        if(value === "SCOT") {
            return "Bardzo dobry";
        } else if ( value === "Piotr")  {
            return "Średni";
        } else
        {
            return value;
        }
    }

}
