@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'my-first-project';
  public name: any = "";
  public lastName: any = "";
  public age: any = "";
  public position: any = "";
  public isVisible: boolean = false;
